import * as React from 'react';

interface Language {
  id: number;
  name: string;
  level: string;
  image: string;
  description: string;
}

const languages: Language[] = [
  {
    id: 1,
    name: "English",
    level: "Beginner to Advanced",
    image: "/languages/english.jpg",
    description: "Master English with native speakers"
  },
  {
    id: 2,
    name: "Spanish",
    level: "All Levels",
    image: "/languages/spanish.jpg",
    description: "Learn Spanish through immersive sessions"
  },
  {
    id: 3,
    name: "French",
    level: "Beginner to Intermediate",
    image: "/languages/french.jpg",
    description: "Discover French language and culture"
  },
  {
    id: 4,
    name: "German",
    level: "All Levels",
    image: "/languages/german.jpg",
    description: "Comprehensive German courses"
  },
  {
    id: 5,
    name: "Mandarin",
    level: "Beginner to Advanced",
    image: "/languages/mandarin.jpg",
    description: "Learn Chinese with expert tutors"
  },
  {
    id: 6,
    name: "Japanese",
    level: "All Levels",
    image: "/languages/japanese.jpg",
    description: "Explore Japanese language and culture"
  }
];

const Languages: React.FC = () => {
  return (
    <div className="bg-white py-24 sm:py-32">
      <div className="mx-auto max-w-7xl px-6 lg:px-8">
        <div className="mx-auto max-w-2xl lg:max-w-4xl">
          <h2 className="text-3xl font-bold tracking-tight text-gray-900 text-center">
            Language Courses
          </h2>
          <p className="mt-6 text-lg leading-8 text-gray-600 text-center">
            Explore our wide range of language courses taught by expert native speakers.
          </p>

          <div className="mx-auto mt-20 grid max-w-2xl grid-cols-1 gap-x-8 gap-y-16 sm:grid-cols-2 lg:grid-cols-3">
            {languages.map((language) => (
              <div 
                key={language.id} 
                className="group relative overflow-hidden rounded-2xl bg-white shadow-lg transition-all duration-300 hover:-translate-y-2 hover:shadow-xl"
              >
                <div className="aspect-[4/3] overflow-hidden">
                  <img
                    src={language.image}
                    alt={`${language.name} course`}
                    className="h-full w-full object-cover transition-transform duration-500 group-hover:scale-110"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/50 to-transparent opacity-60 group-hover:opacity-70 transition-opacity duration-300" />
                </div>

                <div className="absolute bottom-0 left-0 right-0 p-6 text-white">
                  <h3 className="text-xl font-semibold mb-2 group-hover:text-primary-coral transition-colors duration-300">
                    {language.name}
                  </h3>
                  <p className="text-sm text-white/80 mb-2">
                    {language.level}
                  </p>
                  <p className="text-sm text-white/90">
                    {language.description}
                  </p>
                  <button className="mt-4 px-6 py-2 bg-primary-coral text-white rounded-lg font-semibold 
                                   opacity-0 transform translate-y-4 transition-all duration-300 
                                   group-hover:opacity-100 group-hover:translate-y-0
                                   hover:bg-white hover:text-primary-coral">
                    Learn More
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Languages;
