import * as React from "react";
import { NavItemProps } from "../types";

export const NavItem: React.FC<NavItemProps> = ({ label, isHighlighted }) => {
  const baseStyles = "px-4 py-2 font-bold transition-all duration-300 hover:text-primary-coral";
  const highlightedStyles = isHighlighted 
    ? "px-6 py-2 text-red-400 bg-white rounded-full shadow-md hover:shadow-lg transform hover:-translate-y-0.5" 
    : "hover:scale-105";

  return (
    <button
      className={`${baseStyles} ${highlightedStyles}`}
      role="menuitem"
      tabIndex={0}
    >
      {label.toUpperCase()}
    </button>
  );
};
